#include<iostream>
#include<assert.h>
#include<vector>
#include <utility>
#include <string>
#include <unordered_map>
using namespace std;
// 通过hash
// 执行用时：92 ms, 在所有 C++ 提交中击败了58.71%的用户
// 内存消耗：10.9 MB, 在所有 C++ 提交中击败了13.93%的用户
int firstUniqChar1(string s) {
    int nSize = s.size();
    unordered_map<char, int> map;
    for(int i = 0; i < nSize; i++)
    {
        map[s[i]] += 1;
    }

    for(int i = 0; i < nSize; i++)
    {
        if (map[s[i]] == 1)
        {
            return i;
        }
    }

    return -1;
}

// 使用数组会优化很多
// 执行用时：20 ms, 在所有 C++ 提交中击败了99.50%的用户
// 内存消耗：10.7 MB, 在所有 C++ 提交中击败了27.22%的用户
int firstUniqChar(string s) {
    int nSize = s.size();
    int cArray[26] = {0};
    for(int i = 0; i < nSize; i++)
    {
        cArray[s[i] - 'a']++;
    }

    for(int i = 0; i < nSize; i++)
    {
        if (cArray[s[i] - 'a'] == 1)
        {
            return i;
        }
    }

    return -1;
}

// 由于都是小写字母，我们可以直接开辟一个char[27]数组
int main()
{
    string s = "";
    firstUniqChar(s);
    return 0;
}